mapper.update:
update memo
set title = #{title}
,updated_at = current_timestamp
where imemo = #{imemo}
and iuser = #{iuser}

update memo
set ctnt = #{ctnt}
,updated_at = current_timestamp
where imemo = #{imemo}
and iuser = #{iuser}

mapper.select:
select title, ctnt
from memo
where imemo = #{imemo}
and iuser = #{iuser}

mapper.update:
update memo
set del_yn = 1
where imemo = #{imemo}
and iuser = #{iuser}
