mapper.update:

UPDATE timer
SET study_line = #{studyLine}
, isticker = CASE WHEN #{studyLine} <= 24 THEN 2
WHEN #{studyLine} <= 48 THEN 3
WHEN #{studyLine} <= 72 THEN 4
WHEN #{studyLine} <= 96 THEN 5
ELSE 6 END
WHERE iuser = #{iuser}
AND itimer = #{itimer}

mapper.select:
SELECT * FROM timer
WHERE DAY(dead_line) = #{day}
AND MONTH(dead_line) = #{month};
