mapper.update:
update todo
set
isticker = #{isticker}
,updated_at = current_timestamp
where itodo =#{itodo}

update todo
set
title = #{title},
ctnt = #{ctnt},
icategory = #{icategory}
where itodo = #{itodo}

mapper.select:
SELECT * FROM TODO
WHERE created_at BETWEEN DATE_ADD(NOW(), INTERVAL -1 MONTH )
AND NOW() AND iuser =#{iuser}
AND DAY(created_at) = #{day};

Select *from todo
where iuser =#{iuser}
and day(created_at) = #{day}
and month(created_at) =#{month}
